CREATE TABLE beers (
  ID int PRIMARY KEY,
  brewery_id int NOT NULL default 0,
  name nvarchar(max) NOT NULL default '',
  style_id int NOT NULL default 0,
  abv float NOT NULL default 0,
  srm float NOT NULL default 0,
  upc int NOT NULL default 0,
  descript nvarchar(max) NOT NULL,
)

CREATE TABLE breweries (
  ID int PRIMARY KEY,
  name nvarchar(255) NOT NULL default '',
  address1 nvarchar(255) NOT NULL default '',
  address2 nvarchar(255) NOT NULL default '',
  city nvarchar(255) NOT NULL default '',
  state nvarchar(255) NOT NULL default '',
  code nvarchar(25) NOT NULL default '',
  country nvarchar(255) NOT NULL default '',
  phone nvarchar(50) NOT NULL default '',
  website nvarchar(255) NOT NULL default '',
  descript text NOT NULL,
)

CREATE TABLE breweries_geocode (
  ID int PRIMARY KEY,
  brewery_id int NOT NULL default '0',
  latitude float NOT NULL default '0',
  longitude float NOT NULL default '0',
  accuracy nvarchar(255) NOT NULL default ''
) 

CREATE TABLE styles (
  ID int PRIMARY KEY,
  style_name nvarchar(255)NOT NULL default ''
)

