INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (28, 935, 'Odell IPA', 31, 7, 0, 0, 'We took the traditional IPA, originally shipped from England to India in the 1700''s, and made it bolder and more flavorful - American Style. We''ve added new varieties of highly aromatic American hops to create a distinctive bitterness profile and an incredible hop character.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (29, 1271, 'Ornery Amber Lager', 95, 0, 0, 0, 'Ornery Amber is brewed with a blend of the finest European hops and gently roasted malts.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (30, 935, 'Cutthroat Porter', 25, 4.8, 0, 0, 'Not quite a stout but definitely no lightweight, Cutthroat Porter is smooth and robust. Inspired by the classic London porters, we use dark roasted malts to create a deep, rich color and flavor that hint at chocolate and coffee. We named it Cutthroat Porter as our tribute to the Colorado state fish - with its own rich heritage and unmistakable dark coloring. And while we''re big fans of small batches, here''s to the currently threatened Cutthroat population reaching mass quantities.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (31, 1271, 'Maple Nut Brown Ale Ale', 38, 0, 0, 0, 'Maple syrup is added to each barrel of Maple Nut Brown Ale to impart roasted sweetness balancing the nut flavor produced by chocolate malts.')
