INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (819, 1115, 'Urbock 23°', 92, 9.6, 0, 0, 'Schloss Eggenberg Urbock 23° is one of the strongest beers in the world. We keep the Urbock 23° in our Schloss cellars for 9 months until it is dark gold and strongly matured. Urbock 23° has received the highest acknowledgments and honours at international exhibitions and world evaluations. It is brewed exclusively from natural raw ingredients after the purity requirement of 1516. Schloss Eggenberg Urbock is filled in a 0.33 litre designer bottle embossed with Schloss Eggenberg and in barrels for the export (20 and 30 litre).')
