INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (981, 409, 'Crabby Larry''s Irish Red Ale', 22, 6, 0, 0, 'Rich and smooth on the pallate this ale is a near perfect "Irish Red". A variety of hops, Mt. Hood, Liberty & Fuggles combined with some roasted barley make an agreeable brew.')
