INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1007, 700, 'Pig Iron Porter', 25, 0, 0, 0, 'Historically, porter had its heyday in 18th century London, where it was the beer of the working class. It doesn''t take much to work up a thirst for this flavorful beer. The distinct roasted and chocolate notes are well balanced by a slight bitterness.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1008, 700, 'Ironbound Ale', 26, 5.1, 0, 0, 'Hold onto your beer mugs, because this medium-bodied beer has been overwhelmed with hop additions. Brewed as an American pale ale, it''s a celebration of American hop varieties: Cascade, Crystal, Centennial and Chinook. You won''t be disappointed.')
