INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1010, 700, 'Anvil Ale', 5, 4.8, 0, 0, 'English style best bitter. It is a traditional English pub ale that is easy drinking, often referred to as a "session beer". It is medium-bodied and copper colored with a noticeable malt flavor up front that finishes with a hop bitterness and floral hop flavor.')
