INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1174, 869, 'Hope & King Scotch Ale', 15, 6, 0, 0, 'Hope and King is a full-body brew and is rich in malt complexity. Brewed with both English and American barley and many, many specialty malts this deeply colored ale has hints of roasted chocolate, caramel and raisins with very little hop presence. Our interpretation of the classic ale that originated in Glasgow, Scotland.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1175, 869, 'West Bank Pub Ale', 1, 4.6, 0, 0, 'Most welcoming British pubs off a house ale or "pub ale" — this is ours. We use only the finest English barley, hops and yeast to create this classic English-style pale ale. It is noted for its nice malt flavor balanced with a medium hop presence. The pub ale is often referred to as our house "session beer" (a beer you can drink a number of and not fall off your barstool).')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1176, 869, 'Bright Spot Golden Ale', 37, 4.5, 0, 0, 'This American-style golden ale is our lightest, in both color and flavor. Bright Spot is brewed with locally malted 2-row barley and the finest American, Czech and German hops lending a well-balanced, refreshing taste. A compliment to nearly any of our menu choices.')
