INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1238, 524, 'Double Barrel Ale', 33, 5, 0, 0, 'A British Pale Ale never tasted so fresh. We challenge our good friends across the pond to match this one. Can’t be done. We’ve honored the traditions of the great British Pale brewers of Burton-on-Trent using our patented Firestone Union oak barrels. You’re left with a mild blend of vanilla and toasted oak flavor touched with an elegant hint of English noble hops. DBA is the flagship of our company and wildly popular.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1239, 404, 'Island Pale Ale (IPA)', 31, 7, 0, 0, 'Our Islander IPA is a powerful West Coast IPA. This beer bites back with an intense hoppy zing and high alcohol content. The Islander IPA is dry hopped with tons of Centennial & Chinook hops and its fruity aroma will win your approval.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1240, 404, 'Point Loma Porter', 25, 0, 0, 0, 'A robust porter, very dark in color and a roasted malt flavor from chocolate malts. Its slight bitterness is rightfully balanced with a sweet smooth finish. A full-bodied beer with medium hop bitterness and aroma.')
