INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1256, 29, 'Pure Hoppiness', 32, 8, 0, 0, 'A West Coast Double IPASo mega-hopped it will take you to hop heaven. We’ve used hops in the boil, more hops in the giant hopback, and added to that, an incredible amount of dry-hopping for that cutting-edge “hop bite.” Once you’ve tasted this unique beer, all others pale in comparison. 1.072 OG Classified IBU 8%ABV')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1257, 1204, 'Smoked Porter', 25, 5.9, 0, 0, 'Stone Smoked Porter is dark, rich and complicated. A porter is substantially darker than a pale ale, but not as black or opaque as a stout. Our Smoked porter has a captivatingly deep mahogany color, complimented by silky dark tan head. Rich, full bodied and robust. Smooth, with chocolate and coffee-like flavors balanced by the subtle "smoky" character of just the right amount of peat-smoked specialty malt.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1258, 1204, 'Stone Pale Ale', 26, 5.4, 0, 0, 'Our flagship ale, Stone Pale Ale is our Southern California interpretation of the classic British pale ale style. Deep amber in color, Stone Pale Ale is robust and full flavored. A delicate hop aroma is complemented by a rich maltiness. This is an ale for those who have learned to appreciate distinctive flavor. Stone Pale Ale is great by itself, or with food that requires a beer of character.')
