INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1362, 255, 'Bridgeport India Pale Ale', 31, 5.5, 0, 0, 'Our award-winning IPA sparkles with effervescence, the result of natural-conditioning, a process where the beer is double-fermented in each bottle, keg or cask. Brewed with a blend of five hop varieties, BridgePort IPA presents a floral, citrusy aroma and full hop flavor, while downplaying the bitterness.  The beer pours smooth in the glass, emitting its signature golden glow.')
