INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1710, 1181, 'Hefe Weiss', 50, 4.2, 0, 0, 'This coarse-filtered wheat ale is fermented with a German yeast culture for a refreshingly light spiciness and hints of citrus fruit. A cloudy appearance and an immense creamy head are characteristic of this lightly hopped Bavarian Brew.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1711, 1181, 'Winter Brew', 90, 5.75, 0, 0, 'A flavorful blend of dark roasted and sweet caramel malts defines this smooth and robust lager. The rich, nourishing flavors of a full-bodied Munich bock make this Bavarian-style brew perfect for those long winter nights.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (1712, 1181, 'Black Bavarian Lager', 86, 5.86, 0, 0, 'This intensely dark Kulmbacher style lager has a superb malt complexity with the distinctive flavors and aromas of coffee, caramel and chocolate.A renowned smoothness and a creamy, tan head make it a world champion.')
