INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (2630, 358, 'Mud Puppy Porter', 25, 0, 0, 0, 'A robust, yet surprisingly refreshing porter, Mud Puppy is a favorite of dark beer lovers. Characterized by a thick, rocky head and luscious chocolate-like nose, the malty profile is balanced by liberal hopping for its style.')
