INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (3558, 259, 'Brooklyn Lager', -1, 5.2, 0, 0, 'Brooklyn Lager, the Brewery''s flagship label, is New York''s "hometown" beer, brewed to a pre-Prohibition recipe that dates back to the days when Brooklyn was the brewing capital of the East Coast. Brooklyn Lager has won numerous awards. Wrote Michael Jackson in the Simon & Schuster Pocket Guide to Beer: "The dry-hopped, fresh, flowery, firm, flavourful, Brooklyn Lager **-*** started well, in 1988, and has gained in character since."')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (3559, 520, 'Base Camp Golden Ale', 37, 4.9, 0, 0, 'The lightest of FiftyFifty''s ales, Base Camp is a thirst quenching delight. Pale straw to light gold in color, this beer has a mild malt flavor combined with light bitterness and hop flavor. This beer finishes dry and is very light and refreshing on the palate. Base Camp is a "session beer", meaning you can easily toss back a few in one sitting. So go ahead, pull up a chair and camp out for awhile.')
