INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4238, 1357, 'Avenue Ale', 26, 4.1, 0, 0, '2007 Maryland Governor''s Cup Bronze Medal:A golden colored ale with crisp malt character and a slightly spicy hop aroma. We use a good amount of wheat and some German malt in this one to give it a very refreshing taste. Avenue Ale is a true session beer, so sit down and prepare for a long one.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4239, 544, 'Copper Head Ale', 33, 0, 0, 0, '')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4240, 423, 'Geary''s Pale Ale', 4, 4.5, 0, 0, '"A classic British pale ale with a nod to the legendary beers of Burton-on-Trent. Copper-colored, dry, clean and crisp with lots of late hope taste in an appetizing complex with ale fruitiness from imported Hampshire yeast." ~ http://www.gearybrewing.com/pages/pale.php')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4241, 650, 'Aecht Schlenkerla Rauchbier Urbock', 90, 6, 0, 0, 'A smoked bock beer for Bamberg''s strong beer season (October through December). Matured for months in ancient rock-cellars underneath Bamberg and tapped freshly from the oakwood cask. Similar to, but much bigger than the classic Maerzen style.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4242, 616, 'Grolsch Amber Ale', 57, 5.3, 0, 0, '')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4243, 616, 'Grolsch Premium Weizen', 116, 5.5, 0, 0, 'Grolsch Premium Weizen is the main wheat beer made by Grolsch.It is made according to the German Reinheitsgebot ("German Beer Purity Law"), meaning that it is made exclusively with (wheat)malt, water, hoppes and barley.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4244, 616, 'Grolsch Dunkel Weizen', 116, 5.5, 0, 0, 'Grolsch Dunkel Weizen is the darker version of Grolsch Premium Weizen.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4245, 616, 'Grolsch Pilsner Speciale', 77, 5.5, 0, 0, 'Grolsch Speciale Pilsner is a special version of the Grolsch Premium Pilsner. It has a fruitier taste than the Premium Pilsner.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4246, 616, 'Grolsch Premium Pilsner', 77, 5, 0, 0, 'The ''standard'' Grolsch beer.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4247, 713, 'Sneck Lifter', 29, 5.1, 0, 0, 'A dark beer with a reddish tinge derived from the use of coloured malts, full of complex flavours, which create an intriguing beer of great character.In Northern English dialect sneck means door latch and a sneck lifter was a man’s last sixpence which enabled him to lift the latch of a pub door and buy himself a pint, hoping to meet friends there who might treat him to one or two more.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4248, 378, 'Chimay Grand Reserve(Chimay Blue)', -1, 9, 0, 0, 'Named Grande Réserve in 75 cl (25.4 fl.oz.) bottles, it is principally distinguished by its character of a strong beer. This is a beer whose fragrance of fresh yeast with a light, flowery rosy touch is especially pleasant. Its flavour, noticed when tasting it, only accentuates the pleasant sensations perceived in the aroma , while revealing a light but pleasant touch of roasted malt.This top fermented Trappist beer , refermented in the bottle, is not pasteurised.')
