INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4282, 23, 'Allagash White', 67, 5, 0, 0, 'Our interpretation of a traditional Belgian wheat beer, Allagash White is unique and truly refreshing. Brewed with a generous portion of wheat and our own special blend of spices, this beer is light and slightly cloudy in appearance, with a spicy aroma. Overall, it is a beer that is very drinkable and smooth any time of the year.Available in: 12 oz and 750 ml bottles, 15.5 and 5.17 gal kegsABV: 5.0%Original Gravity: 1048Recommended Serving Temp: 34°F to 50°F')
