INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4290, 328, 'Dry Hop Orange', -1, 5.4, 0, 0, 'A conundrum in the world of beer.  This ale does not fit any conventional beer style.  Orange in color only, this beer has hop characteristics of oranges and tangerines.')
