INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4297, 10, 'Purple Haze', 116, 0, 0, 0, 'Purple Haze is a crisp, American style wheat beer with raspberry puree added after filtration. Therefore, you may see raspberry pulp in the beer. The raspberries provide the lager with a subtle purple coloration and haze, a fruity aroma, and a tartly sweet taste.')
