INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4318, 83, 'Duveneck''s Dortmunder', 95, 4.5, 0, 0, 'Frank Duveneck was a famous Cincinnati painter who produced such masterpieces as Whistling Boy andOld Man In a Fur Cap. Our Dortmunder is a medium- bodied golden lager showcasing a delicious malt profile balanced with Noble German hops. This beer is designed to satisfy even the stodgiest of old beer curmudgeons!')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4319, 83, 'Cumberland Pale Ale', 26, 4.6, 0, 0, 'Early Cincinnati brewers shipping their beers into Tennessee would generously hop their beer to prevent spoilage during the long arduous trip over the Cumberland Trail. This classic interpretation of the American Pale Ale features a solid foundation of pale malt loaded with fresh Cascade hops. Here’s to getting where you’re going!')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4320, 83, 'RedLegg Ale', 33, 4.9, 0, 0, 'In the old days, runners who made it to third base were rewarded with a mug of beer! This is the way the game was meant to be played; pitchers throwing side- armed and a half barrel on the field. Our Red Legg Ale is an American Amber Ale with a rich, malty flavor, slightly sweet on the finish. An All-Star in any line- up!')
