INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4386, 44, 'Michelob Ultra Amber', 95, 5, 0, 0, 'Michelob ULTRA Amber is an American-style amber lager that boasts a beautifully rich, dark-amber color with a full-bodied, malty taste that also is low in calories and carbohydrates.')
