INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4399, 486, 'stella', -1, 4.5, 0, 0, 'Stella is the most famous brand of beer in Egypt. It has been manufactured in Egypt since the 19th century and so many Egyptian, and actually many experienced travelers, prefer it to any other kind of beer. One will not find a single bar in Egypt that doesn’t sell Stella or even have the Stella logo on the bar or shop. Stella can even be found in many bars in Europe')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4400, 1322, 'Kasteel Blonde', 37, 11, 0, 0, 'Slightly dangerous beer - tastes like an 8% blonde, delivers like nothing else. A little tart around the edges, due to the higher alcohol content, but otherwise a pleasant strong pils taste : not too much hop on the palate.')
