INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4403, 1375, 'Hobgoblin', 29, 5.5, 0, 0, '')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4404, 304, 'Piraat 10.5%', 37, 10.5, 0, 0, '"Piraat is a ''living'' beer, which means that after the primary fermentation in the keg, the beer also continues to evolve during the secondary fermentation in the bottle or in the keg after packaging. This is a world-class amber colored beer. American beer connoisseurs give it 98 out of 100. No other beer scores better. The flavor is so complex and so rich that every swallow conjures up new associations. Note the spicy light sweetness, which is richly balanced with the robust bitterness of the hops. It is an adventure of a beer, a treat."')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4405, 1308, 'SuperBock', 90, 5, 0, 0, '"Authentic Flavour" Super Bock is the leading beer brand on the Portuguese market and is the only brand to have won 19 consecutive gold medals in the international contest "Monde Sélection da la Qualité')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4406, 144, 'Toasted Lager', 95, 5.3, 0, 0, 'TOASTED LAGERBlue Point Brewing''s award-winning Toasted Lager is our flagship product. Copper in color this brew is made from six different malts including: English Pale, Crystal, Munich, Carapils, Wheat and Belgian Caravienna. Toasted Lager displays a balanced flavor of malt and hop which makes for easy drinking. Special lager yeast is used to produce that long lasting, smooth finish. The "toasted" part of the name refers to our direct-fire brew kettle’s hot flames that impart a toasted flavor to our most popular microbrew.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4407, 586, 'Goose Island IPA', 31, 5.9, 0, 0, 'From the Goose Island website:"Our IPA recalls a time when ales shipped from England to India were highly hopped to preserve their distinct taste during the long journey. The result, quite simply a hop lover''s dream. And this classic ale adds a fruity aroma, set off by a dry malt middle, to ensure that the long hop finish is one you''ll remember."')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4408, 95, 'Beck´s', 77, 4.5, 0, 0, 'German Pilsner beer, with quite a bit of hops taste.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4409, 480, 'Schlappeseppl Export', 95, 5.5, 0, 0, 'A classically brewed export beer with a slight aroma of hops.Awarded with the gold medal byGerman Agriculture Society-DLG2005, 2006 and 2007Monde Selection, Brüssel 2003 and 20052007 with Grand Goldand with the silver medal byWorld Beer Cup, Colorado 2002')
