INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4682, 1013, 'Kate The Great Russian Imperial Stout', 43, 9.5, 0, 0, 'The granddaddy of stouts.  Intended for export to the Imerial Court of Russia''s Catherine the Great.  Jet black.  Full-bodied.  Very strong.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4683, 1260, 'Dark Lord Russian Imperial Stout', 43, 13, 0, 0, 'Gargantuan Russian Stout brewed with coffee, molasses, and honey.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4684, 1244, 'Light Weight', 37, 4.33, 0, 0, 'Our lightest beer, and our Bronze Medal Winner 2006 World Beer Cup German pilsner malt and American hops give this brew a light, crisp finish. Made with the light beer drinker in mind.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4685, 1244, 'Donovan''s Red', 33, 5.16, 0, 0, 'Named after Donovan''s, a 19th century Irish restaurant in downtown Waterbury. This medium-bodied, red-colored ale has a nice hop flavor and aroma.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4686, 1244, 'Pappy''s Porter', 25, 5.42, 0, 0, 'This dark mahogany ale has agreat and delicious malt depth, yet is balanced with supple, energizing bitterness.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4687, 1244, 'Holy Cow IPA', 31, 5.16, 0, 0, 'For hops lovers - this bitter ale is pale colored and made with a blend of 6 different malts and 5 different hops. This ale is dry-hopped for extra flavor and aroma.')
