INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4690, 1244, 'El Jefe', 31, 5.7, 0, 0, 'Our holiday ale. This I.D.A., India Dark Ale, is crisp, bitter and overflowing with the aroma of spruce. Perfect for the holidays.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4691, 1244, 'Heady Topper', 32, 8, 0, 0, 'Is our double I.P.A.! Loaded with hops, this one will put hair on your chest.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4692, 1244, 'Heathen', 31, 6.9, 0, 0, 'An american IPA brewed with a blend of 3 base malts and a multitude of hops.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4693, 1244, 'Hellbrook', 33, 8, 0, 0, 'This American double red ale is packed with enough hops to balance the full malty body.')
