INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4695, 1244, 'Mortal Sin', 31, 6.66, 0, 0, 'This I.P.A. is so rich and delicious that you''ll need to confess after drinking one! Generously dry-hopped for your olefactory pleasure.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4696, 1244, 'Piston Bitter', 5, 5, 0, 0, 'A full bodied special bitter brewed with British malt and American hops')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4697, 1244, 'Rapture', -1, 0, 0, 0, '')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4698, 1244, 'Revitalization Rye', 119, 6.5, 0, 0, 'This IPA is brewed with 35% rye malt. The crisp malt character is layered with Warrior hops. Dry-hopped with Warrior and Simcoe.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4699, 1244, 'Shut The Hell Up', 10, 3.3, 0, 0, 'An American mild ale brewed with Horizon hops. The crisp, bitter finish is helpd up by a balanced malt body.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4700, 296, 'St. Bernardus Witbier', 67, 5.5, 0, 0, 'This traditional Witbier (Wheat beer) has been developed in cooperation with Master Brewer Pierre Celis, the Godfather of Hoegaarden and Celis White. This beer as well has a second fermentation in the bottle, giving this beer its specific taste (5.5% alcohol content).')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4701, 169, 'Petite Orval', 72, 3.5, 0, 0, '')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (4702, 378, 'Chimay Dorée', 72, 4.8, 0, 0, 'Brewed from very similar ingredients as the Red, but paler and spiced differently. It is intended only to be drunk at the abbey or at the nearby inn Auberge de Poteaupré which is associated with the abbey. The monks themselves drink this variety rather than the stronger three. The Dorée is not sold commercially and the rare bottles which make their way out are through unofficial sources. Even the brewery''s own web site makes no mention of this variety.')
