INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5076, 1125, 'Sea Dog Blue Paw Wheat', 121, 3.5, 0, 0, 'Our unique contribution to the fruit ale category features the nutty quench of wheat ale combined with the delightful aromatics and subtle fruit flavor contributed by Maine wild blueberries.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5077, 1031, 'Pyramid Broken Rake', 33, 6.1, 0, 0, 'Mahogany in color, this full-bodied ale features a floral aroma and malty finish for a taste that''s the perfect accompaniment to a crisp autumn afternoon.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5078, 490, 'Elk Creek Copper Ale', 33, 0, 0, 0, 'This beautifully colored ale strikes an agreeable balance between malt and hops. Caramel + toffee notes from the use of more highly kilned malts step up to complement this beers assertive hop profile.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5079, 490, 'Great Blue Heron Pale Ale', 26, 0, 0, 0, 'Much like the local icon it is named for, our version of American Pale Ale has a bold character. We add a healthy dose of hops in both the kettle and the finishing tank.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5080, 490, 'Winkleblink Ale', 116, 0, 0, 0, 'This light colored ale gains its subtle, bread-like flavor from a balance of gently kilned malted barley and choice malted wheat. A restrained addition of hops, contributes a pleasingly delicate floral aroma.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5081, 490, 'Brookie Brown Ale', 38, 0, 0, 0, 'Proof that all dark beers, like trout, are not created equal. Chocolate and caramel malt flavors are at the heart of this very accessible and drinkable brown ale.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5082, 490, 'Poe Paddy Porter', 25, 0, 0, 0, 'Named for our beloved State Park this Robust Porter draws its uniquely enticing roasted character from a thoughtful combination of malts. Generous hop additions ensure a satisfying beer drinking experience.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5083, 490, 'Double Rainbow IPA', 2, 0, 0, 0, 'Named for the spectacular and complete double rainbow that appeared in the northwest sky just outside the brewhouse during the inaugural mash in. With a foot in both the new and old worlds, this IPA is generously dry-hopped with English Fuggles, resulting in a strong, deep golden, very fresh, and fruity English flavor and aroma.')
