INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5340, 338, 'Capital Bavarian Lager', -1, 0, 0, 0, 'Munich Helles style Lager')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5341, 402, 'Potbelly Porter', 25, 0, 0, 0, 'Our Porter has a deep dark color and a light creamy head. The aroma and flavor lingers of burnt grains and roasted coffe. We hope you enjoy this special brew.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5342, 402, 'Celebration Wheat', 116, 0, 0, 0, 'A nice traditional cloudy wheat beer. It is light in color with a banana and yeasty fragrance. This would go nice on a hot summer day or anytime you want a refreshing drink.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5343, 402, 'Lucky 393 Grand Cru', 66, 0, 0, 0, 'This beer is medium dark in color but it has a light body and a smooth mouthfeel. Coriander and orange will surround your senses and keep you coming back for more.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5344, 402, 'Eislin Dubbel', 60, 8, 0, 0, 'The Dubbel is a nice brown creamy sweet, malty and strong ale. It is a traditional Belgian dubbel ale with a secondary fermentation. This complex ale has many flavor characteristics. At 8% abv you will notice a slight alcohol warmth at the finish .')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5345, 409, 'Dead Man Walkin'' IPA', 31, 6.4, 0, 0, 'This extra hoppy ale is characteristic of the classic pale ales. Using the very flavorful Mount Hood and Liberty hops we have created classic IPA goodness every sailor knows.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5346, 409, 'Anne Bonny Irish Stout', -1, 3.9, 0, 0, 'How about trying a glass of traditional Irish stout. Our stout however is not particularly dry and has overtones of dark chocolate and toffee. The infused nitrogen gives a wonderful cascading effect in the glass and a rich, creamy head. Particularly tasty when paired with our oysters on the half shell.')
