INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5406, 426, 'Crooked Tree IPA', 31, 6, 0, 0, 'Inspired by West Coast I.P.A.''s, but brewed with Michigan style. The Crooked Tree is heavily dry hopped to give it a big aroma of pine and citrus. The flavors are big, yet very balanced between fresh hops and malt. Often described as "grapefruit" our hops give this beer an excellent fruit flavor that finishes dry, crisp, and clean. It will pour a nice deep copper color with a bit of haziness. Because of our almost patented "Intense Transfer Methods" our Crooked Tree has won several medals in the India Pale Ale category.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5407, 487, 'El Toro Negro Oatmeal Stout', 21, 0, 0, 0, '')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5408, 783, 'Tumbleweed IPA', 31, 5.7, 0, 0, 'A GOLD MEDAL WINNER at the Great American Beer Festival, beating out 98 other IPA''s and chosen as the best IPA in the country! It''s amber color and incredible hoppy aroma will keep you coming back for more. R-U-HOPPY?')
