INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5439, 1150, 'Steel Toe Stout', 20, 5.4, 0, 0, 'This traditional English Cream Stout is brewed with actual milk sugar to create a creamy and sweet brew. Jet black in color, the latte frothy head will make you mooo for more.')
