INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5484, 765, 'Ruben & The Jets', 30, 8.6, 0, 0, 'This Ale is Brewed in Honor of the 40th Anniversary Release of this Album.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5485, 594, 'Old Faithful Ale', 26, 0, 0, 0, 'ur Old Faithful Ale is a Pale Golden Ale with a crisp body and light malt sweetness. We cold condition this ale to give it a pleasantly smooth character and dry palate. The Willamette and imported UK Goldings hops give this beer a light floral hop aroma, making it exceptionally easy to drink.')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5486, 349, 'Cottonwood Endo India Pale Ale', 31, 5.9, 0, 0, 'This brew is a favorite for those who love hops! This medium copper-colored ale is dry hopped for a strong hop nose with medium malt overtones and hint of caramel that blend for one great tasting beer!')
INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5487, 325, 'Olde Buzzard Lager', 95, 5, 0, 0, 'Our Lager (Dortmund-style) is brewed with pale, Munich and Vienna malts and is moderately hopped with Hallertauer hops. Dortmund is an industrial, steel-making town and its lagers are hearty, easy drinking brewskies. The hops balance the malt but not overtly so. A great brew for the end of a hard day at work. Winner of the Gold Medal for Best European Style Pilsner at the 2000 Great American Beer Festival , Gold Medal at the 5th and 6th Great International Beer Festival, Silver Medals at the 7th Great International Beer Festival and 9th Great International Beer Festival, and Bronze Medal at the 8th Great International Beer Festival and 11th Great International Beer Festival!')
