INSERT INTO beers (id, brewery_id, name, style_id, abv, srm, upc, descript) VALUES (5871, 1246, 'Loakal Red', 33, 6.9, 0, 0, '75% American Red Ale, 25% Ale Aged in American Oak Barrels.  A brash American Red ale, dripping with citrusy Centennial hops, mellowed by a touch of oak.  Aromas of toffee, citrus, crushed herbs, vanilla and fresh sawn oak.  Complex!')
