INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(2, '21st Amendment Brewery Cafe', '563 Second Street', '', 'San Francisco', 'California', '94107', 'United States', '1-415-369-0900', 'http://www.21st-amendment.com/', 'The 21st Amendment Brewery offers a variety of award winning house made brews and American grilled cuisine in a comfortable loft like setting. Join us before and after Giants baseball games in our outdoor beer garden. A great location for functions and parties in our semi-private Brewers Loft. See you soon at the 21A!')
