INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(378, 'Chimay (Abbaye Notre Dame de Scourmont)', 'Route de Charlemagne 8', '', 'Chimay', '', '', 'Belgium', '+32 (0)60 21 03 27', 'http://www.chimay.com', 'Chimay is an authentic Trappist beer that is it is brewed within a Trappist monastery, under the control and responsibility of the monastic community. Only 6 beers in Belgium can carry the appellation Trappist: Chimay, Orval, Rochefort, Westmalle, Westvleteren and Achel.')
