INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(381, 'City Brewing Company, LLC', '925 South Third Street', '', 'La Crosse', 'Wisconsin', '54601', 'United States', '1-608-785-4200', 'http://www.citybrewery.com/', 'City Brewing Company is a premier, state-of-the-art beverage production and packaging company located in La Crosse, WI.  Our facility has been transformed from a historical brewery into a facility capable of manufacturing and packaging beers, teas, soft drinks, energy drinks and other new age beverages.  Our packaging capacity of over 50 million cases makes us one of the largest beverage producers in the country.')
