INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(526, 'Fish Brewing Company & Fish Tail Brewpub', '515 Jefferson Street SE', '', 'Olympia', 'Washington', '98501', 'United States', '1-360-943-3650', 'http://www.fishbrewing.com/', 'From our home in beautiful Olympia, Washington, Fish Brewing Company has been hand-crafting ales of Northwest proportions since 1993. Then, using a system cobbled from brewing and dairy equipment, we brewed for our neighbors up and down Puget Sound. Growing steadily since, Fish is now an award-winning craft brewer with distribution throughout the Pacific Northwest and beyond.')
