INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(657, 'High Point Wheat Beer Company', '22 Park Place', '', 'Butler', 'New Jersey', '7405', 'United States', '(973) 838-7400', 'http://www.ramsteinbeer.com/', 'Founded in 1994, the High Point Brewing Company is the first exclusive wheat beer brewery in America. The founder and driving force of High Point is award-winning homebrewer, Greg Zaccardi. After working as a brewer in southern Germany, Greg returned to the US to open his brewery. His Ramstein beers are made with the same care and precision he learned in Germany. In fact, the wheat, barley, hops and yeast used in Ramstein are all imported directly from Bavaria.')
INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(658, 'Highland Brewing Company', '12 Old Charlotte Highway', '', 'Asheville', 'North Carolina', '28803', 'United States', '(828) 299-7223', 'http://www.highlandbrewing.com/', 'Nestled in the Blue Ridge Mountains in Asheville, North Carolina, Highland Brewing Company brews the finest and freshest ales in the Southeast.  For more than a decade, we have produced an impressive array of ales, from the best-selling Gaelic to our popular winter seasonal, Cold Mountain Ale.')
