INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(780, 'Legacy Brewing Co.', '545 Canal Street', '', 'Reading', 'Pennsylvania', '19602', 'United States', '(610) 376-9996', 'http://www.legacybrewing.com', 'Legacy Brewing Co. is committed to handcrafting unique, high quality beer by employing brewing techniques learned from European and American brewers. The complexity of flavors comes from a combination of high quality imported malts and our hopping methods which includes the use of whole flower hops. The Legacy team includes a creative collection of brewers, engineers, a microbiologist and our friends in the arts community; all with a passion for innovative products. Legacy...we brew pure pleasure!')
