INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(920, 'North Country Brewery', '141 South Main Street', '', 'Slippery Rock', 'Pennsylvania', '16057', 'United States', '(724)-794-BEER', 'http://www.northcountrybrewing.com', 'Nestled in the heart of downtown Slippery Rock, North Country Brewery typically features 10-15 beers that rotate seasonally.  While this unique restaurant and brewery inhabits a former funeral home, it is quite lively inside.  Featuring a great menu and fun events, North Country Brewery is a great stop for any beer lover.')
