INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(1193, 'Standing Stone Brewing Company', '101 Oak Street', '', 'Ashland', 'Oregon', '97520', 'United States', '(541) 482-2448', 'http://www.standingstonebrewing.com/', 'Ashland''s only full service brewpub is housed in an historic building with a light industrial character. We offer our own freshly brewed handcrafted ales and lagers, including seasonal and other specialty brews.  All Standing Stone Ales are made here at the brewery in ten barrel batches using over 80% organic malts.')
