INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(1253, 'The Round Barn Winery & Brewery', '10983 Hills Road', '', 'Baroda', 'Michigan', '49101', 'United States', '(800)-716-9463', 'http://www.roundbarnwinery.com/brewery.php', 'With more than 30 years of fermentation experience and an award-winning reputation in the industry, our line of hand-crafted brews embody the satisfying bold taste you''ve come to expect from The Round Barn.')
