INSERT INTO breweries(ID, name, address1, address2, city, state, code, country, phone, website, descript) VALUES(1309, 'Union Barrel Works', '6 N. Reamstown Road', '', 'Reamstown', 'Pennsylvania', '17567', 'United States', '717-335-7837', 'http://www.unionbarrelworks.com/', 'Union Barrel Works is a restaurant and brew pub located in Reamstown, Pennsylvania in an historic building which originally functioned as a hardware store. It later became Reamstown Garment and was a sewing factory. The building itself was built in 1911 and still has the original hardwood maple floors and authentic tin ceiling. Come visit us for a great atmosphere, better food and excellent beer!')
